DROP TABLE IF EXISTS stories;

-- Create posts table

CREATE TABLE stories (
  id serial primary key,
  title text not null,
  content text not null,
  created timestamp DEFAULT now()
);

ALTER SEQUENCE stories_id_seq RESTART WITH 1000;

